def get_marker1(string: str) -> int:

    for i in range(3, len(string)):
        if string[i] not in string[i-3 : i]:
            # return i+1
            if str_count(string[i-3 : i]) == 0:
                return i+1

def str_count(string: str) -> int:
    count = 0
    check_string = ''
    for char in string:
        if char not in check_string:
            check_string += char
        else:
            count += 1
    return count

def get_marker2(string: str) -> int:

    for i in range(13, len(string)):
        if string[i] not in string[i-13 : i]:
            # return i+1
            if str_count(string[i-13 : i]) == 0:
                return i+1

string = ''
print(get_marker1(string))
print(get_marker2(string))